import React from 'react';
import {
  Box,
  AppBar,
  Toolbar,
  Typography,
  styled,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  ListItemButton,
  Divider,
} from '@mui/material';
import { Link, useLocation } from 'react-router-dom';
import HomeIcon from '@mui/icons-material/Home';
import ExploreIcon from '@mui/icons-material/Explore';
import StarIcon from '@mui/icons-material/Star';
import SettingsIcon from '@mui/icons-material/Settings';
import UserPanel from './UserPanel';

const DRAWER_WIDTH = 240;

const StyledLink = styled(Link)`
  color: white;
  text-decoration: none;
`;

const menuItems = [
  { text: '发现新角色', icon: <HomeIcon />, path: '/' },
  { text: '探索世界', icon: <ExploreIcon />, path: '/explore' },
  { text: '我的收藏', icon: <StarIcon />, path: '/favorites' },
  { text: '设置', icon: <SettingsIcon />, path: '/settings' },
];

interface LayoutProps {
  children: React.ReactNode;
}

const Layout = ({ children }: LayoutProps) => {
  const location = useLocation();
  const isChatPage = location.pathname.includes('/chat/');

  return (
    <Box sx={{ display: 'flex', bgcolor: '#F8FAFC' }}>
      {!isChatPage && (
        <AppBar 
          position="fixed" 
          sx={{ 
            zIndex: (theme) => theme.zIndex.drawer + 1,
            bgcolor: '#7E57C2',
            boxShadow: 'none',
            height: 48,
          }}
        >
          <Toolbar sx={{ minHeight: '48px !important' }}>
            <Typography variant="subtitle1" component="div" sx={{ flexGrow: 1, fontWeight: 600 }}>
              <StyledLink to="/">AI Companion</StyledLink>
            </Typography>
          </Toolbar>
        </AppBar>
      )}

      {!isChatPage && (
        <Drawer
          variant="permanent"
          sx={{
            width: DRAWER_WIDTH,
            flexShrink: 0,
            '& .MuiDrawer-paper': {
              width: DRAWER_WIDTH,
              boxSizing: 'border-box',
              bgcolor: '#ffffff',
              borderRight: '1px solid #e0e0e0',
              mt: '48px',
              height: 'calc(100vh - 48px)',
            },
          }}
        >
          <List sx={{ flex: 1, pt: 1 }}>
            {menuItems.map((item) => (
              <ListItem key={item.text} disablePadding>
                <ListItemButton
                  component={Link}
                  to={item.path}
                  selected={location.pathname === item.path}
                  sx={{
                    minHeight: 44,
                    borderRadius: '0 24px 24px 0',
                    mr: 2,
                    '&.Mui-selected': {
                      bgcolor: '#EDE7F6',
                      color: '#7E57C2',
                      '& .MuiListItemIcon-root': {
                        color: '#7E57C2',
                      },
                    },
                    '&:hover': {
                      bgcolor: '#F5F5F5',
                      '&.Mui-selected': {
                        bgcolor: '#EDE7F6',
                      },
                    },
                  }}
                >
                  <ListItemIcon sx={{ minWidth: 36 }}>{item.icon}</ListItemIcon>
                  <ListItemText 
                    primary={item.text} 
                    primaryTypographyProps={{
                      fontSize: '0.875rem',
                      fontWeight: location.pathname === item.path ? 600 : 400,
                    }}
                  />
                </ListItemButton>
              </ListItem>
            ))}
          </List>
          <Divider sx={{ my: 1 }} />
          <UserPanel />
        </Drawer>
      )}

      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: isChatPage ? 0 : 3,
          width: '100%',
          marginTop: isChatPage ? 0 : '48px',
          bgcolor: '#f8f9fa',
          minHeight: '100vh',
        }}
      >
        {children}
      </Box>
    </Box>
  );
};

export default Layout;
