import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  TextField,
  IconButton,
  Typography,
  Avatar,
  Paper,
  Divider,
  Button,
  Chip,
} from '@mui/material';
import {
  Send as SendIcon,
  ArrowBack as ArrowBackIcon,
} from '@mui/icons-material';

interface Message {
  id: number;
  text: string;
  sender: 'user' | 'ai';
  timestamp: Date;
}

const SIDEBAR_WIDTH = 300;

const Chat = () => {
  const { characterId } = useParams();
  const navigate = useNavigate();
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState('');

  // 模拟角色数据
  const character = {
    id: characterId,
    name: '埃隆·马斯克',
    avatar: 'https://via.placeholder.com/150',
    description: '特斯拉和SpaceX的CEO',
    tags: ['企业家', '科技', '创新'],
    background: '埃隆·马斯克是一位科技企业家、工程师和投资者。他是特斯拉公司的CEO和首席工程师，SpaceX的创始人、CEO和首席工程师。他还创立了The Boring Company，并且是Neuralink和OpenAI的联合创始人。',
    traits: [
      { label: '创新思维', value: 95 },
      { label: '技术洞察', value: 90 },
      { label: '商业头脑', value: 88 },
      { label: '领导能力', value: 85 },
    ],
  };

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    const userMessage: Message = {
      id: Date.now(),
      text: newMessage,
      sender: 'user',
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setNewMessage('');

    // 模拟AI回复
    setTimeout(() => {
      const aiMessage: Message = {
        id: Date.now() + 1,
        text: '这是一个模拟的AI回复消息。',
        sender: 'ai',
        timestamp: new Date(),
      };
      setMessages(prev => [...prev, aiMessage]);
    }, 1000);
  };

  return (
    <Box sx={{ height: '100vh', display: 'flex' }}>
      {/* 左侧角色信息栏 */}
      <Box
        sx={{
          width: SIDEBAR_WIDTH,
          borderRight: '1px solid',
          borderColor: 'divider',
          bgcolor: 'white',
          display: 'flex',
          flexDirection: 'column',
          overflow: 'auto',
        }}
      >
        <Box sx={{ p: 3 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <IconButton 
              onClick={() => navigate(-1)}
              sx={{ mr: 1 }}
            >
              <ArrowBackIcon />
            </IconButton>
            <Typography variant="h6">角色信息</Typography>
          </Box>

          <Box sx={{ textAlign: 'center', mb: 3 }}>
            <Avatar
              src={character.avatar}
              sx={{ 
                width: 120, 
                height: 120, 
                mx: 'auto',
                mb: 2,
                bgcolor: 'primary.light',
              }}
            />
            <Typography variant="h5" sx={{ fontWeight: 600, mb: 0.5 }}>
              {character.name}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {character.description}
            </Typography>
          </Box>

          <Box sx={{ mb: 3 }}>
            <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 600 }}>
              标签
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
              {character.tags.map(tag => (
                <Chip 
                  key={tag} 
                  label={tag} 
                  size="small" 
                  variant="outlined"
                />
              ))}
            </Box>
          </Box>

          <Box sx={{ mb: 3 }}>
            <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 600 }}>
              背景介绍
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {character.background}
            </Typography>
          </Box>

          <Box>
            <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 600 }}>
              性格特征
            </Typography>
            {character.traits.map(trait => (
              <Box key={trait.label} sx={{ mb: 1 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
                  <Typography variant="body2">{trait.label}</Typography>
                  <Typography variant="body2" color="text.secondary">
                    {trait.value}%
                  </Typography>
                </Box>
                <Box
                  sx={{
                    height: 4,
                    bgcolor: 'rgba(0, 0, 0, 0.08)',
                    borderRadius: 2,
                    overflow: 'hidden',
                  }}
                >
                  <Box
                    sx={{
                      height: '100%',
                      width: `${trait.value}%`,
                      bgcolor: 'primary.main',
                      borderRadius: 2,
                    }}
                  />
                </Box>
              </Box>
            ))}
          </Box>
        </Box>
      </Box>

      {/* 聊天区域 */}
      <Box sx={{ 
        flex: 1, 
        display: 'flex', 
        flexDirection: 'column',
        borderLeft: '1px solid',
        borderColor: 'divider',
      }}>
        {/* 消息列表 */}
        <Box 
          sx={{ 
            flex: 1, 
            overflow: 'auto',
            bgcolor: '#f8f9fa',
            p: 3,
          }}
        >
          {messages.map((message) => (
            <Box
              key={message.id}
              sx={{
                display: 'flex',
                justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start',
                mb: 2,
              }}
            >
              {message.sender === 'ai' && (
                <Avatar
                  src={character.avatar}
                  sx={{ width: 32, height: 32, mr: 1 }}
                />
              )}
              <Paper
                sx={{
                  p: 1.5,
                  maxWidth: '70%',
                  bgcolor: message.sender === 'user' ? 'primary.main' : 'white',
                  color: message.sender === 'user' ? 'white' : 'text.primary',
                  borderRadius: 2,
                  boxShadow: message.sender === 'user' 
                    ? '0 2px 8px rgba(126, 87, 194, 0.15)' 
                    : '0 2px 8px rgba(0, 0, 0, 0.05)',
                }}
              >
                <Typography variant="body1">
                  {message.text}
                </Typography>
              </Paper>
            </Box>
          ))}
        </Box>

        {/* 输入框 */}
        <Paper 
          component="form" 
          onSubmit={handleSendMessage}
          elevation={0}
          sx={{ 
            p: 2,
            borderTop: '1px solid',
            borderColor: 'divider',
            bgcolor: 'white',
          }}
        >
          <Box sx={{ display: 'flex', gap: 1 }}>
            <TextField
              fullWidth
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="输入消息..."
              variant="outlined"
              size="small"
              autoComplete="off"
            />
            <IconButton 
              type="submit" 
              color="primary"
              disabled={!newMessage.trim()}
            >
              <SendIcon />
            </IconButton>
          </Box>
        </Paper>
      </Box>
    </Box>
  );
};

export default Chat;
